import styled from "styled-components";
export const Wrapper = styled.section`
  width: 100hw;
  max-width: 100hw;
  height: 100vw;
  max-height: 100vw;
  background: green;
  display: flex;
  flex-wrap: no-wrap;
  justify-content: center;
`;

export const DynamicSection = styled.section`
  width: 50%
  max-width: 60%;
  height: 100%;
  position: relative;

`;

export const ImageContainer = styled.div`
  width: 100%;
  height: 400px;
  background-position: 0px ${({ right }) => (right ? 0 : "-600px")};
  background: url(${({ bg }) => bg}) 0 0 no-repeat;
  position: absolute;
  top: 0;
  display: block;
  z-index: 1;
  overflow: hidden;
`;
