import { useState, useEffect } from "react";
import {
  Wrapper,
  LeftHalf,
  RightHalf,
  ImgWrapper,
  BackgroundAnimation,
  GridItem,
  Title,
  SubTitle,
  Image,
} from "./App.styles";

import thingiverseImg from "./assets/thingiverse.png";
import instagramImg from "./assets/instagram.png";
const App = () => {
  const [mousePos, setMousePos] = useState({ x: 50, y: 50 });
  const isMaker = mousePos.x < 30;
  const isCoder = mousePos.x > 30;

  useEffect(() => {
    const handleMouseMove = (event) => {
      setMousePos({
        x: Math.floor((event.clientX / window.innerWidth) * 100),
        y: Math.floor((event.clientY / window.innerHeight) * 100),
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <Wrapper>
      <GridItem row={4} column={"2/4"} isSelected={isMaker}>
        <Title>MAKER</Title>
      </GridItem>
      <GridItem row={"5/6"} column={"2/4"} isSelected={isMaker} isHidden>
        <SubTitle isSelected={isMaker}>
          3d Maker who love printing and creating things
        </SubTitle>
      </GridItem>
      <GridItem row={"6/7"} column={"2"} isSelected={isMaker} isHidden>
        <Image alt="thingiverseLink" src={thingiverseImg} />
      </GridItem>
      <GridItem row={"6/7"} column={"3"} isSelected={isMaker} isHidden>
        <Image alt="thingiverseLink" src={instagramImg} />
      </GridItem>

      <GridItem row={4} column={"8/10"}>
        <Title isSelected={isCoder}>&#60;CODER &#62;</Title>
      </GridItem>
      <GridItem row={"5/6"} column={"8/10"} isSelected={isMaker} isHidden>
        <SubTitle isSelected={isMaker}>
          Front end developer who writes clean, elegant and efficient code.
        </SubTitle>
      </GridItem>
      <GridItem row={"6/7"} column={"8"} isSelected={isCoder} isHidden>
        <Image alt="thingiverseLink" src={thingiverseImg} />
      </GridItem>

      <BackgroundAnimation mousePos={mousePos.x}>
        <ImgWrapper>
          <LeftHalf />
          <RightHalf position={mousePos.x} />
        </ImgWrapper>
      </BackgroundAnimation>
    </Wrapper>
  );
};

export default App;
