import styled from "styled-components";
export const Wrapper = styled.section`
  width: 100vw;
  height: 100vh;
  position: relative;
  display: grid;
  grid-template-columns: repeat(12, 1fr);
`;

export const BackgroundAnimation = styled.div`
  position: absolute;
  width: 100%;
  bottom: 0;
`;
export const ImgWrapper = styled.section`
  height: 600px;
  width: 800px;
  position: relative;
  margin: 0 auto;
`;

export const LeftHalf = styled.div`
  background: url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/about-adham-dannaway.jpg")
    no-repeat right center;
  background-size: 800px 600px;
  width: 800px;
  top: 0;
  margin: auto;
  position: absolute;
  height: 600px;
`;
export const RightHalf = styled.div`
  background: url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/adham-dannaway-half-face.jpg")
    no-repeat left center;
  background-size: 800px 600px;
  position: absolute;
  top: 0;
  margin: auto;
  height: 600px;
  width: ${({ position }) => position}%;
  max-width: 800px;
`;
