import { useState, useEffect } from "react";
import {
  Wrapper,
  LeftHalf,
  RightHalf,
  ImgWrapper,
  BackgroundAnimation,
} from "./App.styles";

const App = () => {
  const [mousePos, setMousePos] = useState({ x: 50, y: 50 });

  useEffect(() => {
    const handleMouseMove = (event) => {
      // console.log(event);
      // console.log(window.innerWidth);

      setMousePos({
        x: Math.floor((event.clientX / window.innerWidth) * 100),
        y: Math.floor((event.clientY / window.innerHeight) * 100),
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <Wrapper>
      <BackgroundAnimation>
        <ImgWrapper>
          <LeftHalf />
          <RightHalf position={mousePos.x} />
        </ImgWrapper>
        {/* <Image
        src="https://www.adhamdannaway.com/wp-content/themes/dannaway/images/adham-dannaway-half-face.jpg"
        position={mousePos.x}
      />
      <Image
        src="https://www.adhamdannaway.com/wp-content/themes/dannaway/images/about-adham-dannaway.jpg"
        position={mousePos.x}
        right
      /> */}
      </BackgroundAnimation>
    </Wrapper>
  );
};

export default App;
