import { useState, useEffect } from "react";
import { useMediaQuery } from "usehooks-ts";
import {
  Wrapper,
  LeftHalf,
  RightHalf,
  ImgWrapper,
  BackgroundAnimation,
  GridItem,
  Title,
  SubTitle,
  Image,
} from "./App.styles";

import thingiverseImg from "./assets/thingiverse.png";
import instagramImg from "./assets/instagram.png";
import githubImg from "./assets/github.png";

const App = () => {
  const [mousePos, setMousePos] = useState({ x: 50, y: 50 });
  const laptopUp = useMediaQuery("(min-width: 1024px)");
  console.log(laptopUp);
  const isMaker = mousePos.x < 30;
  const isCoder = mousePos.x > 30;

  useEffect(() => {
    const handleMouseMove = (event) => {
      setMousePos({
        x: Math.floor((event.clientX / window.innerWidth) * 100),
        y: Math.floor((event.clientY / window.innerHeight) * 100),
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <Wrapper>
      <GridItem row={4} column={"2/4"} isSelected={isMaker}>
        <Title>MAKER</Title>
      </GridItem>
      <GridItem row={"5/7"} column={"2/4"} isSelected={isMaker} isHidden>
        <SubTitle isSelected={isMaker}>
          3d Maker who love printing and creating things
        </SubTitle>
      </GridItem>
      <GridItem row={"6/7"} column={"2"} isSelected={isMaker} isHidden>
        <a href="https://www.thingiverse.com/elegidoadedo/designs">
          <Image alt="thingiverseLink" src={thingiverseImg} />
        </a>
      </GridItem>
      <GridItem row={"6/7"} column={"3"} isSelected={isMaker} isHidden>
        <a href="https://www.instagram.com/stories/highlights/18259807864185887/?hl=es">
          <Image alt="instagramLink" src={instagramImg} />
        </a>
      </GridItem>

      <GridItem row={4} column={"8/-1"} isSelected={isCoder}>
        <Title isSelected={isCoder}>&#60;CODER &#62;</Title>
      </GridItem>
      <GridItem row={"5/6"} column={"8/10"} isSelected={isCoder} isHidden>
        <SubTitle isSelected={isCoder}>
          Front end developer who writes clean, elegant and efficient code.
        </SubTitle>
      </GridItem>
      <GridItem row={"6/7"} column={"8"} isSelected={isCoder} isHidden>
        <a href="https://github.com/Elegidoadedo">
          <Image alt="githubLink" src={githubImg} />
        </a>
      </GridItem>

      <BackgroundAnimation mousePos={mousePos.x}>
        <ImgWrapper>
          <LeftHalf />
          <RightHalf position={mousePos.x} />
        </ImgWrapper>
      </BackgroundAnimation>
    </Wrapper>
  );
};

export default App;
