import { useState, useEffect } from "react";
import {
  Wrapper,
  LeftHalf,
  RightHalf,
  ImgWrapper,
  BackgroundAnimation,
  GridItem,
  Title,
  SubTitle,
} from "./App.styles";

const App = () => {
  const [mousePos, setMousePos] = useState({ x: 50, y: 50 });
  const isMaker = mousePos.x < 30;
  const isCoder = mousePos.x > 30;

  useEffect(() => {
    const handleMouseMove = (event) => {
      setMousePos({
        x: Math.floor((event.clientX / window.innerWidth) * 100),
        y: Math.floor((event.clientY / window.innerHeight) * 100),
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <Wrapper>
      <GridItem row={4} column={"2/4"}>
        <Title isSelected={isMaker}>MAKER</Title>
        <SubTitle>I create and pirnt things </SubTitle>
      </GridItem>
      <GridItem row={4} column={"8/10"}>
        <Title isSelected={isCoder}>&#60;CODER &#62;</Title>
      </GridItem>

      <BackgroundAnimation mousePos={mousePos.x}>
        <ImgWrapper>
          <LeftHalf />
          <RightHalf position={mousePos.x} />
        </ImgWrapper>
      </BackgroundAnimation>
    </Wrapper>
  );
};

export default App;
