import { useState, useEffect } from "react";
import { Wrapper, DynamicSection } from "./App.styles";

const App = () => {
  const [mousePos, setMousePos] = useState({ x: 50, y: 50 });

  useEffect(() => {
    const handleMouseMove = (event) => {
      // console.log(event);
      // console.log(window.innerWidth);

      setMousePos({
        x: Math.floor((event.clientX / window.innerWidth) * 100),
        y: Math.floor((event.clientY / window.innerHeight) * 100),
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    return () => {
      window.removeEventListener("mousemove", handleMouseMove);
    };
  }, []);

  return (
    <Wrapper>
      <DynamicSection bg="red" position={mousePos}></DynamicSection>
      <DynamicSection bg="blue" position={mousePos} right></DynamicSection>
    </Wrapper>
  );
};

export default App;
