import styled from "styled-components";
export const Wrapper = styled.section`
  min-width: 100vw;
  min-height: 100vh;
  max-width: 100vw;
  min-height: 100vh;
  position: relative;
  display: grid;

  grid-template-columns: repeat(10, 1fr);
  grid-template-rows: repeat(8, 1fr);
`;
export const GridItem = styled.div`
  grid-row: ${({ row }) => row};
  grid-column: ${({ column }) => column};
  z-index: 1;
`;
export const BackgroundAnimation = styled.div`
  grid-row: 2/-1;
  grid-column: 3/6;
  width: 100%;
  z-index: 0;
  transform: translateX(
    ${({ mousePos }) => (mousePos > 60 ? "-80px" : mousePos < 40 ? "80px" : 0)}
  );
  transition: 0.7s ease;
`;

export const Title = styled.h2`
  margin: 0;
  font-size: 40px;
  color: ${({ isSelected }) => (isSelected ? "black" : "grey")};
  transition: color 1s linear;
`;
export const ImgWrapper = styled.section`
  height: 600px;
  width: 780px;
  position: relative;
`;

export const LeftHalf = styled.div`
  background: url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/about-adham-dannaway.jpg")
    no-repeat right center;
  background-size: 780px 600px;
  width: 780px;
  top: 0;
  position: absolute;
  height: 600px;
`;
export const RightHalf = styled.div`
  background: url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/adham-dannaway-half-face.jpg")
    no-repeat left center;
  background-size: 780px 600px;
  position: absolute;
  top: 0;
  margin: auto;
  height: 600px;
  width: ${({ position }) => position}%;
  max-width: 780px;
`;
