import styled from "styled-components";
export const Wrapper = styled.section`
  width: 100hw;
  max-width: 100hw;
  height: 100vw;
  max-height: 100vw;
  background: green;
  display: flex;
  flex-wrap: no-wrap;
`;

export const DynamicSection = styled.section`
  width: ${({ position, right }) => (right ? 100 - position.x : position.x)}%;
  height: 100%;
  background-image: ${({ bg }) => url(bg)};
`;
