import styled from "styled-components";
export const Wrapper = styled.section`
  width: 100%;
  width: 100vw;
  height: 100%;
  height: 100vh;

  display: flex;
  /* flex-wrap: no-wrap; */
  justify-content: space-around;
  position: relative;
  /* overflow: hidden; */
  /* background: linear-gradient(
    90deg,
    url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/adham-dannaway-half-face.jpg")
      50%,
    #5c8f09 50%
  ); */
  /* width: 400px;
  height: 400px; */
  /* background-repeat: no-repeat;
  background-image: url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/adham-dannaway-half-face.jpg"),
    url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/about-adham-dannaway.jpg");
  background-position: center;
  background-size: 100px 100px 200px 200px; */
  /* background: url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/adham-dannaway-half-face.jpg")
      no-repeat center center,
    url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/about-adham-dannaway.jpg")
      no-repeat center center,
    linear-gradient(#eb01a5, #d13531); */
`;

export const Image = styled.img`
  position: absolute;
  margin: auto;
  height: 100%;
  width: ${({ position, right }) => (right ? position : 100 - position)}%;
  overflow: hidden;
`;

export const DynamicSection = styled.section`
  width: 50%;
  max-width: 60%;
  height: 100%;
  position: relative;
`;

export const ImageContainer = styled.div`
  width: ${({ position, right }) => (right ? position.x : 100 - position.x)}%;
  height: 400px;
  background-position: 0px ${({ right }) => (right ? 0 : "-600px")};
  background: url(${({ bg }) => bg}) 0 0 no-repeat;
  position: absolute;
  top: 0;
  display: block;
  z-index: 1;
  overflow: hidden;
`;

export const LeftHalf = styled.div`
  background: url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/about-adham-dannaway.jpg")
    no-repeat center;
  width: 100%;
  position: absolute;
  height: 100%;
`;
export const RightHalf = styled.div`
  background: url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/adham-dannaway-half-face.jpg")
    no-repeat;
  position: absolute;
  /* margin: auto; */
  top: 0;
  left: 400px;
  height: 100%;
  width: ${({ position }) => position}%;
`;
