import styled from "styled-components";
export const Wrapper = styled.section`
  width: 100%;
  width: 100vw;
  height: 100%;
  height: 100vh;
  background: green;
  display: flex;
  /* flex-wrap: no-wrap; */
  justify-content: space-around;
  position: relative;
  /* overflow: hidden; */
  background: linear-gradient(
    90deg,
    url("https://www.adhamdannaway.com/wp-content/themes/dannaway/images/adham-dannaway-half-face.jpg")
      50%,
    #5c8f09 50%
  );
`;

export const Image = styled.img`
  position: absolute;
  margin: auto;
  height: 100%;
  width: ${({ position, right }) => (right ? position : 100 - position)}%;
  overflow: hidden;
`;

export const DynamicSection = styled.section`
  width: 50%;
  max-width: 60%;
  height: 100%;
  position: relative;
`;

export const ImageContainer = styled.div`
  width: ${({ position, right }) => (right ? position.x : 100 - position.x)}%;
  height: 400px;
  background-position: 0px ${({ right }) => (right ? 0 : "-600px")};
  background: url(${({ bg }) => bg}) 0 0 no-repeat;
  position: absolute;
  top: 0;
  display: block;
  z-index: 1;
  overflow: hidden;
`;
