import styled from "styled-components";
export const Wrapper = styled.section`
  width: 100%;
  max-width: 100vw;
  height: 100%;
  max-height: 100vh;
  background: green;
  display: flex;
  /* flex-wrap: no-wrap; */
  justify-content: space-around;
  position: relative;
  /* overflow: hidden; */
`;

export const Image = styled.img`
  position: absolute;
  margin: auto;
  max-height: inherit;
  width: ${({ position, right }) => (right ? position.x : 100 - position.x)}%;
`;

export const DynamicSection = styled.section`
  width: 50%;
  max-width: 60%;
  height: 100%;
  position: relative;
`;

export const ImageContainer = styled.div`
  width: ${({ position, right }) => (right ? position.x : 100 - position.x)}%;
  height: 400px;
  background-position: 0px ${({ right }) => (right ? 0 : "-600px")};
  background: url(${({ bg }) => bg}) 0 0 no-repeat;
  position: absolute;
  top: 0;
  display: block;
  z-index: 1;
  overflow: hidden;
`;
